import {
    ApolloClient,
    InMemoryCache,
    HttpLink,
    from,
} from "@apollo/client";

// Add your Bitquery API URL and headers
const httpLink = new HttpLink({
    uri: "https://graphql.bitquery.io",
    headers: {
        "X-API-KEY": "BQYtiasVJ7cAiyh2ZUOBhJYeLA2KeRya", // Replace with your Bitquery API key
    }
});

const client = new ApolloClient({
    link: from([httpLink]),
    cache: new InMemoryCache(),
});

export default client;
