import React, { useEffect } from 'react';
import { useQuery, gql, useLazyQuery } from '@apollo/client';

// Define the blocks query
const GET_BLOCKS = gql`
  query GetBlocks {
    bitcoin {
      blocks(any: {date: {after: "2024-02-18T17:14:50+0000"}}) {
        height
        blockSize
        transactionCount
      }
    }
  }
`;

// Modify the transactions query to accept variables
const GET_TRANSACTIONS = gql`
  query GetTransactions($gteq: Int, $lteq: Int) {
    bitcoin {
      transactions {
        block(height: {gteq: $gteq, lteq: $lteq}) {
          height
        }
        feeValue
        txSize
      }
    }
  }
`;

// Define the structure of a block and transaction
type Block = {
    height: number;
    blockSize: number;
    transactionCount: number;
};

type Transaction = {
    block: { height: number };
    feeValue: number;
    txSize: number;
};

// Adjust the expected structure of the data to match your queries
type BlocksData = {
    bitcoin: {
        blocks: Block[];
    };
};

type TransactionsData = {
    bitcoin: {
        transactions: Transaction[];
    };
};

type MergedBlockData = Block & {
    feeValue?: number;
    txSize?: number;
};
// Adjust the BlocksComponent
const BlocksComponent: React.FC = () => {
    const { loading: loadingBlocks, error: errorBlocks, data: dataBlocks } = useQuery<BlocksData>(GET_BLOCKS);
    const [getTransactions, { called, loading: loadingTransactions, data: dataTransactions }] = useLazyQuery<TransactionsData>(GET_TRANSACTIONS);

    useEffect(() => {
        if (dataBlocks) {
            const heights = dataBlocks.bitcoin.blocks.map(block => block.height);
            const minHeight = Math.min(...heights);
            const maxHeight = Math.max(...heights);
            getTransactions({ variables: { gteq: minHeight, lteq: maxHeight } });
        }
    }, [dataBlocks, getTransactions]);

    if (loadingBlocks || !called || loadingTransactions) return <p>Loading...</p>;
    if (errorBlocks) return <p>Error: {errorBlocks.message}</p>;
    if (!dataTransactions || !dataBlocks) return <p>No transaction data available</p>;

    // Assume dataTransactions is now available and proceed to merge
    const mergedData = dataBlocks.bitcoin.blocks.map((block: Block) => {
        const transactionData = dataTransactions.bitcoin.transactions.find(tx => tx.block.height === block.height);
        return {
            ...block,
            feeValue: transactionData ? transactionData.feeValue : 'N/A',
            txSize: transactionData ? transactionData.txSize : 'N/A',
        };
    });

    return (
        <table>
            <thead>
                <tr>
                    <th>Height</th>
                    <th>Block Size</th>
                    <th>Transaction Count</th>
                    <th>Fee Value</th>
                    <th>Transaction Size</th>
                </tr>
            </thead>
            <tbody>
                {mergedData.map((item, index) => (
                    <tr key={index}>
                        <td>{item.height}</td>
                        <td>{item.blockSize}</td>
                        <td>{item.transactionCount}</td>
                        <td>{item.feeValue}</td>
                        <td>{item.txSize}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default BlocksComponent;
