import {
    ApolloClient,
    InMemoryCache,
    HttpLink,
    from,
} from "@apollo/client";

const cache = new InMemoryCache({
    typePolicies: {
        Query: {
            fields: {
                bitcoin: {
                    merge(existing = {}, incoming) {
                        return { ...existing, ...incoming };
                    },
                },
            },
        },
    },
});

// Update this to point to your server's proxy endpoint
const httpLink = new HttpLink({
    uri: "http://localhost:3000/api/graphql", // Point this to your server endpoint
});

const client = new ApolloClient({
    link: from([httpLink]),
    cache,
});

export default client;
