import React from 'react';
import { useQuery, gql } from '@apollo/client';

// Define the blocks query
const GET_BLOCKS = gql`
  query GetBlocks {
    bitcoin {
      blocks(any: {date: {after: "2024-02-18T17:14:50+0000"}}) {
        height
        blockSize
        transactionCount
      }
    }
  }
`;

// Define the transactions query
const GET_TRANSACTIONS = gql`
  query GetTransactions($gteq: Int, $lteq: Int) {
    bitcoin {
      transactions {
        block(height: {gteq: $gteq, lteq: $lteq}) {
          height
        }
        feeValue
        txSize
      }
    }
  }
`;

// Define the structure of a block and transaction
type Block = {
    height: number;
    blockSize: number;
    transactionCount: number;
};

type Transaction = {
    block: { height: number };
    feeValue: number;
    txSize: number;
};

// Adjust the expected structure of the data to match your queries
type BlocksData = {
    bitcoin: {
        blocks: Block[];
    };
};

type TransactionsData = {
    bitcoin: {
        transactions: Transaction[];
    };
};

type MergedBlockData = Block & {
    feeValue?: number;
    txSize?: number;
};


const BlocksComponent: React.FC = () => {
    const { loading: loadingBlocks, error: errorBlocks, data: dataBlocks } = useQuery(GET_BLOCKS);
    //const { loading: loadingTransactions, error: errorTransactions, data: dataTransactions } = useQuery(GET_TRANSACTIONS);
    const [getTransactions, { called, loading, data: transactionsData }] = useLazyQuery(GET_TRANSACTIONS);

    // Check if either query is loading
    if (loadingBlocks || loadingTransactions) return <p>Loading...</p>;

    // Check if either query has an error
    if (errorBlocks) return <p>Error: {errorBlocks.message}</p>;
    if (errorTransactions) return <p>Error: {errorTransactions.message}</p>;

    // Merge the data from both queries
    const mergedData: MergedBlockData[] = dataBlocks.bitcoin.blocks.map((block: Block) => {
        // Find the corresponding transaction data
        const transactionData = dataTransactions.bitcoin.transactions.find((tx: Transaction) => tx.block.height === block.height);
        // Return a new object that combines the block and transaction data
        return {
            ...block,
            feeValue: transactionData ? transactionData.feeValue : 'N/A', // Use 'N/A' or some default value if not found
            txSize: transactionData ? transactionData.txSize : 'N/A',
        };
    });

    // Render the merged data in a table
    return (
        <table>
            <thead>
                <tr>
                    <th>Height</th>
                    <th>Block Size</th>
                    <th>Transaction Count</th>
                    <th>Fee Value</th>
                    <th>Transaction Size</th>
                </tr>
            </thead>
            <tbody>
                {mergedData.map((item: MergedBlockData, index: number) => (
                    <tr key={index}>
                        <td>{item.height}</td>
                        <td>{item.blockSize}</td>
                        <td>{item.transactionCount}</td>
                        <td>{item.feeValue}</td>
                        <td>{item.txSize}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default BlocksComponent;
