import React from 'react';
import { ApolloProvider } from '@apollo/client';
import client from './apollo-client'; // ensure this is set up correctly as per previous instructions
import BlocksComponent from './BlocksComponent'; // this will be our component to display blocks

const App: React.FC = () => {
    return (
        <ApolloProvider client={client}>
            <div className="App">
                <h1>Bitcoin Block Hashes</h1>
                <BlocksComponent />
            </div>
        </ApolloProvider>
    );
};

export default App;
