import React from 'react';
import { useQuery, gql } from '@apollo/client';

const GET_BLOCKS = gql`
  query MyQuery {
  bitcoin {
    blocks(any: {date: {after: "2024-02-18T17:14:50+0000"}}) {
      height
      blockSize
      transactionCount
    }
  }
}
`;

const BlocksComponent: React.FC = () => {
    const { loading, error, data } = useQuery(GET_BLOCKS);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;

    return (
        <table>
            <thead>
                <tr>
                    <th>Height</th>
                    <th>Block Size</th>
                    <th>Transaction Count</th>
                </tr>
            </thead>
            <tbody>
                {data.bitcoin.blocks.map((block: any, index: number) => (
                    <tr key={index}>
                        <td>{block.height}</td>
                        <td>{block.blockSize}</td>
                        <td>{block.transactionCount}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default BlocksComponent;
