import React, { useEffect } from 'react';
import { useQuery, gql, useLazyQuery } from '@apollo/client';
import './styles.css'

// Define the blocks query
const GET_BLOCKS = gql`
  query GetBlocks {
    bitcoin {
      blocks(any: {date: {after: "2024-02-18T17:14:50+0000"}}) {
        height
        blockSize
        transactionCount
        timestamp {
        iso8601
      }
      }
    }
  }
`;

// Define the transactions query
const GET_TRANSACTIONS = gql`
  query GetTransactions($gteq: Int!, $lteq: Int!) {
    bitcoin {
      transactions {
        block(height: {gteq: $gteq, lteq: $lteq}) {
          height
        }
        feeValue
        txSize
      }
    }
  }
`;


// Define the structure of a block and transaction
type Block = {
    height: number;
    blockSize: number;
    transactionCount: number;
    timestamp: {
        iso8601: string;
    };
 
};

type Transaction = {
    block: { height: number };
    feeValue: number;
    txSize: number;
};

// Adjust the expected structure of the data to match your queries
type BlocksData = {
    bitcoin: {
        blocks: Block[];
    };
};

type TransactionsData = {
    bitcoin: {
        transactions: Transaction[];
    };
};

type MergedBlockData = Block & {
    feeValue?: number | 'N/A';
    txSize?: number | 'N/A';
};

const BlocksComponent: React.FC = () => {
    const { loading: loadingBlocks, error: errorBlocks, data: dataBlocks } = useQuery<BlocksData>(GET_BLOCKS);
    const [getTransactions, { called, loading: loadingTransactions, data: dataTransactions }] = useLazyQuery<TransactionsData>(GET_TRANSACTIONS, {
        fetchPolicy: "cache-and-network"
    });

    const formatTimestamp = (isoString: string) => {
        // Replace 'T' with a space and remove 'Z'
        return isoString.replace('T', ' ').replace('Z', '');
    };

    useEffect(() => {
        if (dataBlocks?.bitcoin?.blocks?.length) {
            // Ensure we have at least 20 blocks, otherwise adjust the logic based on available blocks
            const minHeight = dataBlocks.bitcoin.blocks[0]?.height;
            const maxHeightIndex = Math.min(dataBlocks.bitcoin.blocks.length, 20) - 1;
            const maxHeight = dataBlocks.bitcoin.blocks[maxHeightIndex]?.height;

            if (minHeight !== undefined && maxHeight !== undefined) {
                getTransactions({ variables: { gteq: minHeight, lteq: maxHeight } });
            }
        }
    }, [dataBlocks, getTransactions]);

    if (loadingBlocks || !called || loadingTransactions) return <p>Loading...</p>;
    if (errorBlocks) return <p>Error: {errorBlocks.message}</p>;
    if (!dataTransactions?.bitcoin?.transactions) return <p>No transaction data available</p>;

    const mergedData: MergedBlockData[] = dataBlocks?.bitcoin?.blocks.map(block => {
        const transactionData = dataTransactions?.bitcoin?.transactions.find(tx => tx.block.height === block.height);
        return {
            ...block,
            feeValue: transactionData?.feeValue ?? 'N/A',
            txSize: transactionData?.txSize ?? 'N/A',
        };
    }) ?? [];

    return (
        <div className="table-container">
            <table className="table">
                <thead>
                    <tr>
                        <th className="th">Height</th>
                        <th className="th">Block Size</th>
                        <th className="th">Transaction Count</th>
                        <th className="th">Fee Value</th>
                        <th className="th">Transaction Size</th>
                        <th className="th">Time</th>
                    </tr>
                </thead>
                <tbody>
                    {mergedData.map((item, index) => (
                        <tr className="tr" key={index}>
                            <td className="td">{item.height}</td>
                            <td className="td">{item.blockSize}</td>
                            <td className="td">{item.transactionCount}</td>
                            <td className="td">{item.feeValue}</td>
                            <td className="td">{item.txSize}</td>
                            <td className="td">{formatTimestamp(item.timestamp.iso8601)}</td>
                        </tr>
                    ))}
                </tbody>
                </table>
        </div>
    );
};

export default BlocksComponent;
